$(function() {
    var processing = false;

    $('#download').click(function() {
	if (processing==true) return;
	processing = true;
	var origHtml = $('#download').html();
	setTimeout(function() {
	    $('#download').html(origHtml);
	    processing = false;
	},5000);
	$('#download').html('Generálás...');
    });

});