$(function() {

	$('input[type="checkbox"]').click(function(){
            if($(this).is(":checked")){
                $('#hazifeladatFeltoltesEngedely').addClass('engedelyezve');
            }
            else if($(this).is(":not(:checked)")){
                $('#hazifeladatFeltoltesEngedely').removeClass('engedelyezve');
            }
        });

});