
showUpdateLayer = function() {
    takaroElem = $('#takaro');
    if (takaroElem) takaroElem.show();
    $('#updateWindow').show();
    $('updateForm').prop('tabindex',1);
    $('updateForm').focus();
    $('#updateHeader').text('Haladási napló'); // sic
}

