$(function() {
    $('#haladasiModositando').bind('click', function(event) {
	$target = $(event.target);
	$target.parent('div').toggleClass('haladasiModositando');
    });

    $('table.orarend tbody tr td input[type=checkbox]').click(function() {
	$(this).closest('div').toggleClass('torlesreJelolve');
    }); 

    $('#tankorAjanlo').click(function() {
	$('table#orarend tbody tr td select[name="ORARENDPLUSZ[]"').each(function() {
	    selectElement = this;
	    $(this).find('option[data-hint="1"]').attr('selected','selected');

	})
    }); 

    $('div span.tankorAdat').each(function(a) {
	    elem = $(this);
	    // v = parseFloat($('#sumOraszam').val()) + parseFloat(elem.data('tankorevesoraszam'));
	    v = parseInt($('#sumOraszam').val()) + 1;
	    $('#sumOraszam').val(v);
    });

    $('div.orarendModositoToolbox table.tanarTankor tr td.tankorAdat').hover(function(event) {
	$target = $(event.target);
	$('div.oraKartya span[data-tankorid="'+$target.data('tankorid')+'"]').toggleClass('highlight');
	$('div.oraKartya span[data-tankorid="'+$target.data('tankorid')+'"]').parent('div').toggleClass('highlight');
    });
    $('table#orarend span').hover(function(event) {
	$target = $(event.target);
	$('div.oraKartya span[data-tankorid="'+$target.data('tankorid')+'"]').toggleClass('highlight');
	$('div.oraKartya span[data-tankorid="'+$target.data('tankorid')+'"]').parent('div').toggleClass('highlight');
    });

    $('#tankorHelper').bind('click', function(event) {
	$('div.orarendModositoToolbox table.tanarTankor').toggle();
    });


});
