
showUpdateLayer = function() {
    $('#updateWindow').show();
    $('updateForm').prop('tabindex',1);
    $('updateForm').focus();
}
hideUpdateLayer = function() {
    $('#tanmenet tr').removeClass('aktiv');
}

$(function() {

    $('#tanmenet tr a').click(function(event) {
        var element = $(event.target).closest('tr');
	$('#tanmenet tr').removeClass('aktiv');
	element.addClass('aktiv'); 
    });
});