$(function() {

    $("input.email").focus(function() {
    }).blur(function() {
	if (validateEmail($(this).val()) === false) 
	    alert('Nem megfelelő email cím!');
    });

    $("input[type=submit]").click(function() {
	// ellenőrzés
	// return false;
    });

});

function validateEmail(email) {
    var re = /^(([^<>()[\]\.,;:\s@\"]+(\.[^<>()[\]\.,;:\s@\"]+)*)|(\".+\"))@(([^<>()[\]\.,;:\s@\"]+\.)+[^<>()[\]\.,;:\s@\"]{2,})$/i;
    return re.test(email);
}
